					["ConstructorCode"] = "function (self, unitId, unitFrame, envTable, scriptTable)\n    -- load Shared Media Texture\n    local LibSharedMedia = LibStub:GetLibrary (\"LibSharedMedia-3.0\")\n    local texture = LibSharedMedia:Fetch (\"statusbar\", Plater.db.profile.focus_texture)\n    \n    -- create the Indicator\n    local ImportantTargetIndicator = unitFrame.healthBar:CreateTexture(nil, \"overlay\")\n    \n    -- create overlay over healthbar\n    PixelUtil.SetPoint(ImportantTargetIndicator, \"topleft\",  unitFrame.healthBar, \"topleft\", 0, 0)\n    PixelUtil.SetPoint(ImportantTargetIndicator, \"bottomright\", unitFrame.healthBar, \"bottomright\", 0, 0)\n    \n    -- Set texture and color\n    ImportantTargetIndicator:SetTexture(texture)\n    ImportantTargetIndicator:SetVertexColor (unpack (Plater.db.profile.focus_color))\n    unitFrame.ImportantTargetIndicator = ImportantTargetIndicator\n    \n    unitFrame.ImportantTargetIndicator:Hide()\nend\n\n\n",
